;jQuery(document).ready(function() {
    
    /* Tabs for front page slideshow */
    jQuery('#tabs').tabs({ fx: [{opacity:'toggle', duration:'normal'},                           
                                {opacity:'toggle', duration:'fast'}] }).show();

    /* Squish text on front page audio playlists */
    jQuery('.jwplayer-featured-audio-playlist').parent().addClass('with-playlist').siblings('.frontslide-text').addClass('text-playlist');
                                
	
     /* Show hide embed and download bubbles 
        and make sidebar transparent so they show up more */
    
     jQuery('.tooltip-download').hide();
     jQuery('.tooltip-embed').hide();
    
  	 jQuery('#download-link').click(function() {
        jQuery('.tooltip-download').show();
        jQuery('.tooltip-embed').hide();
        jQuery('#embed-link').removeClass('video-actions-current');
        jQuery(this).addClass('video-actions-current');
        jQuery('#rightsidebar').addClass('sidebar-opacity');
     });
     
     /*jQuery('.tooltip-download-close').click(function() {
        
     });*/
     
     jQuery('#download-link').click(function(event) {
  	     
  	     if(jQuery(event.target).is(".tooltip-download-close") ) {
  	         jQuery('.tooltip-download').hide('10');
             jQuery('#download-link').removeClass('video-actions-current');
             jQuery('#rightsidebar').removeClass('sidebar-opacity');
  	     }     
  	     else {
  	       jQuery('.tooltip-download').show();
           jQuery('.tooltip-embed').hide();
           jQuery('#embed-link').removeClass('video-actions-current');
           jQuery(this).addClass('video-actions-current');
           jQuery('#rightsidebar').addClass('sidebar-opacity');
  	     }  
    
     });
     
     jQuery('#embed-link').click(function(event) {
  	     
  	     if(jQuery(event.target).is(".tooltip-embed-close") ) {
  	         jQuery('.tooltip-embed').hide();
             jQuery('#rightsidebar').removeClass('sidebar-opacity');
  	         jQuery('#embed-link').removeClass('video-actions-current');
  	     }     
  	     else {
  	      jQuery(this).removeClass('link-closed'); 
          jQuery('.tooltip-embed').show();
          jQuery('.tooltip-download').hide();
          jQuery('#download-link').removeClass('video-actions-current');
          jQuery(this).addClass('video-actions-current');
          jQuery('#rightsidebar').addClass('sidebar-opacity');
  	     }  
    
     });
     
     /*jQuery('.tooltip-embed-close').click(function() {
        
          
     });*/
     
     /* Remove bottom border on last item in embed/download links */
     
     jQuery('#video-actions').find('li').last().addClass('li_last');
     
     /* Buttons to automatically copy code */
     /* Todo: hide for non flash eg iphones */
     
     ZeroClipboard.setMoviePath( 'http://devel.srichinmoy.tv/wp-content/themes/ckg-media/js/zeroclipboard/ZeroClipboard.swf' ); 
     var clip = new ZeroClipboard.Client();
     var html1 = clip.getHTML(130, 20);
     jQuery('#ta-html').html(html1);   
     clip.setText(jQuery('#ta-html-textarea').val());
     clip.addEventListener( 'onComplete', my_complete );
     
     var clip2 = new ZeroClipboard.Client();
     var html2 = clip2.getHTML(130, 20);
     jQuery('#ta-drupal').html(html2);   
     clip2.setText(jQuery('#ta-drupal-textarea').val());
     clip2.addEventListener( 'onComplete', my_complete );
     
     var clip3 = new ZeroClipboard.Client();
     var html3 = clip3.getHTML(130, 20);
     jQuery('#ta-wp').html(html3);   
     clip3.setText(jQuery('#ta-wp-textarea').val());
     clip3.addEventListener( 'onComplete', my_complete );      
       	 	
	 /* highlight entire textarea content on focus */
	 	  	  
     jQuery("textarea.share-embed-code").focus(function() {          
          this.select();
     });
     
     /* Adjust textarea values according to width input value */
     
     widthvalue = jQuery('input#video-width').val();
     heightvalue = computeheight(widthvalue);
     
     jQuery('input#video-width').keyup(function() {
        changedwidth = jQuery(this).val();
        changedheight = computeheight(changedwidth);

        jQuery("textarea.share-embed-code").each(function() {
            jQuery(this).text(jQuery(this).text().replace(widthvalue, changedwidth));
            jQuery(this).text(jQuery(this).text().replace(heightvalue, changedheight));
        });
        clip.setText(jQuery('#ta-html-textarea').val());
        clip2.setText(jQuery('#ta-drupal-textarea').val());
        clip3.setText(jQuery('#ta-wp-textarea').val());
            
        widthvalue = changedwidth; 
        heightvalue = changedheight;
     });
     

             

}); 
 
function my_complete( client, text ) {
   alert("The embed code has been copied to your clipboard.");
} 

function computeheight (widthvalue) {
      return widthvalue * playerheight / playerwidth;
}    

